import React from "react";
import "../css/Common.css";

export function Card({
  header,
  body,
  image,
}: {
  header: string;
  body: string;
  image?: JSX.Element;
}) {
  return (
    <div
      style={{
        flex: 1,
        width: "20%",
        alignContent: "center",
        justifyContent: "center",
      }}
      className="card"
    >
      {image}
      <p className="header-text-card">{header}</p>
      <p className="text-small-plus">{body}</p>
    </div>
  );
}
