import React, { useState } from "react";
import { ColorString } from "../ColorPalette";
import "../css/Common.css";

export function SchmoozSubscribeForm({
  status,
  message,
  onValidated,
}: {
  status: "error" | "success" | "sending" | null;
  message: string | Error | null;
  onValidated: (val: any) => void;
}) {
  const [email, setEmail] = useState<string>("");

  const submit = (event: any) => {
    event.preventDefault();
    email &&
      email.indexOf("@") > -1 &&
      onValidated({
        MERGE0: email,
        MERGE5: "",
      });
  };

  return (
    <div style={{ flex: 1, display: "flex" }}>
      <form
        style={{ display: "flex", flexDirection: "column" }}
        onSubmit={(event) => {
          submit(event);
        }}
      >
        <label
          className="text-medium"
          style={{ fontSize: 17, color: ColorString.white, paddingBottom: 5 }}
        >
          Email Address
        </label>
        <div
          style={{
            display: "flex",
            borderWidth: 1,
            width: "100%",
            borderColor: ColorString.lightGrey,
            backgroundColor: ColorString.white,
            borderRadius: 10,
            height: 52,
          }}
        >
          <input
            value={email}
            onChange={(val) => setEmail(val.target.value)}
            style={{
              paddingLeft: 10,
              outlineWidth: 0,
              fontSize: 20,
              border: 0,
              flex: 3,
              width: "60%",
              borderColor: ColorString.lightGrey,
              borderRadius: 10,
              height: 50,
            }}
          />
          <input
            type="submit"
            value="Subscribe"
            className="text-medium"
            style={{
              flex: 1,
              backgroundColor: ColorString.red,
              border: 0,
              color: ColorString.white,
              fontSize: 17,
              height: 52,
              borderRadius: 10,
            }}
          />
        </div>
        {message ? (
          <p
            dangerouslySetInnerHTML={{ __html: message as string }}
            style={{ fontSize: 17, color: ColorString.white, paddingBottom: 5 }}
          >
            {message}
          </p>
        ) : (
          <></>
        )}
      </form>
    </div>
  );
}
