import React from "react";
import "../css/Common.css";

export function Card({
  header,
  body,
  image,
}: {
  header: string;
  body: string;
  image?: JSX.Element;
}) {
  return (
    <div
      style={{
        flex: 1,
        alignContent: "center",
        justifyContent: "center",
        alignItems: "center",
        margin: 10,
      }}
      className="card"
    >
      <div
        style={{
          display: "block",
          marginLeft: "auto",
          marginRight: "auto",
          textAlign: "center",
        }}
      >
        {image}
      </div>

      <p className="header-text-card">{header}</p>
      <p className="text-small-plus">{body}</p>
    </div>
  );
}
