import React from "react";
import "../css/Common.css";

export function Card({
  header,
  body,
  image,
}: {
  header: string;
  body: string;
  image?: JSX.Element;
}) {
  return (
    <div
      style={{
        flex: 1,
        alignContent: "center",
        justifyContent: "center",
        alignItems: "center",
        margin: 10,
      }}
      className="card"
    >
      <div
        style={{
          // margin: "30%",
          width: "60%",
          flex: 1,
          justifyContent: "center",
          alignItems: "center",
          alignSelf: "center",
          alignContent: "center",
          backgroundColor: "red",
        }}
      >
        <p>hi</p>
        {image}
      </div>

      <p className="header-text-card">{header}</p>
      <p className="text-small-plus">{body}</p>
    </div>
  );
}
