import React, { useState } from "react";
import { Colors } from "react-native/Libraries/NewAppScreen";
import { ColorString } from "../ColorPalette";
import "../css/Common.css";

export function SchmoozSubscribeForm({
  status,
  message,
  onValidated,
}: {
  status: "error" | "success" | "sending" | null;
  message: string | Error | null;
  onValidated: (val: any) => void;
}) {
  const [email, setEmail] = useState<string>("");
  const [schmoozOrUse, setSchmoozOrUser] = useState<Boolean | undefined>(
    undefined
  );

  const submit = (event: any) => {
    email &&
      email.indexOf("@") > -1 &&
      onValidated({
        MERGE0: email,
      });
  };

  return (
    <div>
      <form
        style={{ display: "flex", flexDirection: "column" }}
        onSubmit={(event) => submit(event)}
      >
        {status === "success" && (
          <div
            className="mc__alert mc__alert--success"
            dangerouslySetInnerHTML={{ __html: message }}
          />
        )}

        {status !== "success" ? (
          <div className="mc__field-container">// form field code...</div>
        ) : null}

        {status === "success" ? (
          <button
            onClick={() => setModalOpen(false)}
            className="g__justify-self-center"
          >
            Close
          </button>
        ) : (
          <input type="submit" />
        )}

        <label
          className="text-medium"
          style={{ fontSize: 17, color: ColorString.white, paddingBottom: 5 }}
        >
          Email Address
        </label>
        <div
          style={{
            flex: 1,
            display: "flex",
            borderWidth: 1,
            width: 412,
            borderColor: ColorString.lightGrey,
            backgroundColor: ColorString.white,
            borderRadius: 10,
            height: 50,
          }}
        >
          <input
            value={email}
            onChange={(val) => setEmail(val.target.value)}
            style={{
              outlineWidth: 0,
              fontSize: 20,
              border: 0,
              flex: 3,
              width: 412,
              borderColor: ColorString.lightGrey,
              borderRadius: 10,
              height: 50,
            }}
          />
          <input
            type="submit"
            value="Subscribe"
            className="text-medium"
            style={{
              flex: 1,
              backgroundColor: ColorString.red,
              border: 0,
              color: ColorString.white,
              fontSize: 17,
              height: 52,
              borderRadius: 10,
            }}
          />
        </div>
      </form>
    </div>
  );
}
